import React from 'react';
import {calcWinner} from './calcWinner';
import {Box} from './box'; 
import {GameOver} from './gameOver';
import { BrowserRouter, Route, Link, Redirect } from 'react-router-dom';

export class Board extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
          boxes: Array(9).fill(null),
          xIsNext: true,
          status: 'Next player: X',
        };
      }

    renderBox(i) {
      return <Box 
        value={this.state.boxes[i]}
        onClick={() => this.handleClick(i)}
        />;
    }

    handleClick(i) {
        const boxes = this.state.boxes.slice();
        if (calcWinner(boxes) || boxes[i]) {
          return winnerAnnounced();
          }
        boxes[i] = this.state.xIsNext ? 'X' : 'O';
        this.setState({
            boxes: boxes,
            xIsNext: !this.state.xIsNext,
        });
      }

    render() {
        const winner = calcWinner(this.state.boxes);
        let status;
        if (winner) {
          status = 'Winner: ' + winner;
          return winnerAnnounced(status);
        } else {
          status = 'Next player: ' + (this.state.xIsNext ? 'X' : 'O');
        }

      return (
        <div className= "board-wrapper">
          <div className="board">
          <h2 className="board-heading">{status}</h2>
          <div className="board-row">
            {this.renderBox(0)}
            {this.renderBox(1)}
            {this.renderBox(2)}
          </div>
          <div className="board-row">
            {this.renderBox(3)}
            {this.renderBox(4)}
            {this.renderBox(5)}
          </div>
          <div className="board-row">
            {this.renderBox(6)}
            {this.renderBox(7)}
            {this.renderBox(8)}
          </div>
          </div>  
        </div>
      );
    }
  }

  function winnerAnnounced(){
    return  <Redirect to="/gameOver" props={this.state.status} />
  }
