import React from 'react';
import {Board} from './board'; 
import {PlayerIcon} from './playerIcon';
import { BrowserRouter, Route, Link, Redirect } from 'react-router-dom';


export class Game extends React.Component {
  constructor(props){
    super(props);
    this.state = {
      emoji: '',
      emojiList : ['üß†', 'ü¶æ', 'üëµ', 'ü¶π‚Äç‚ôÇÔ∏è', 'üßö‚Äç‚ôÄÔ∏è'],
    };

  }

  renderIcon(emoji) {
    return <PlayerIcon
      icon={emoji}
      onClick={() => this.handleClick(emoji)}
      />;
  }

  handleClick(icon) {
      this.state.emoji= icon;
      this.setState({
        emoji: icon,
    });
    console.log(this.state.emoji);
    startGame(this.state.emoji)


  }



    render() {
      return (
        <div className="game">
          <div className="game-board">
          <div className="board-row">
          {this.state.emojiList.map((emoji) => this.renderIcon(emoji))}
          </div>
            <Board />
          </div>
          <div className="game-info">
            <div>{/* status */}</div>
            <ol>{/* TODO */}</ol>
          </div>
        </div>
      );
    }
  }

  function startGame(emoji){
  return  <Redirect to={{pathname: "/gameStart" , state: {playerIcon: emoji} }} />
}
