  constructor(props){
    super(props);
    this.state = {
      emoji: '',
      emojiList : ['üß†', 'ü¶æ', 'üëµ', 'ü¶π‚Äç‚ôÇÔ∏è', 'üßö‚Äç‚ôÄÔ∏è'],
    };

  }

  renderIcon(emoji) {
    return <PlayerIcon
      icon={emoji}
      onClick={() => this.handleClick(emoji)}
      />;
  }

  handleClick(icon) {
      this.state.emoji= icon;
      console.log(this.state.icon);

  }



    render() {
      return (
        <div className="game">
          <div className="game-board">
          <ul>
          {this.state.emojiList.map((emoji) => <PlayerIcon icon={emoji} customClickEvent={this.handleClick(emoji)}/>)}
          </ul>
            <Board />
          </div>
          <div className="game-info">
            <div>{/* status */}</div>
            <ol>{/* TODO */}</ol>
          </div>
        </div>
      );
    }
  }
