import React from 'react';

import {Box} from './box'; 

export class Board extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
          boxes: Array(9).fill(null),
          xIsNext: true,
        };
      }

    renderBox(i) {
      return <Box 
        value={this.state.boxes[i]}
        onClick={() => this.handleClick(i)}
        />;
    }

    handleClick(i) {
        const boxes = this.state.boxes.slice();
        boxes[i] = 'X';
        this.setState({boxes: boxes});
      }

    render() {
      const status = 'Next player: X';

      return (
        <div>
          <div className="status">{status}</div>
          <div className="board-row">
            {this.renderBox(0)}
            {this.renderBox(1)}
            {this.renderBox(2)}
          </div>
          <div className="board-row">
            {this.renderBox(3)}
            {this.renderBox(4)}
            {this.renderBox(5)}
          </div>
          <div className="board-row">
            {this.renderBox(6)}
            {this.renderBox(7)}
            {this.renderBox(8)}
          </div>
        </div>
      );
    }
  }
