import React from 'react';
import {Board} from './board'; 
import {PlayerIcon} from './playerIcon';
import { BrowserRouter, Route, Link, Redirect } from 'react-router-dom';


export class Game extends React.Component {
  constructor(props){
    super(props);
    this.state = {
      emoji: '',
      emojiList : ['üß†', 'ü¶æ', 'üëµ', 'ü¶π‚Äç‚ôÇÔ∏è', 'üßö‚Äç‚ôÄÔ∏è'],
      pickedIcon : false,
    };

  }

  renderIcon(emoji) {
    return <PlayerIcon
      icon={emoji}
      onClick={() => this.handleClick(emoji)}
      />;
  }

  handleClick(icon) {
      this.state.emoji= icon;
      this.setState({
        emoji: icon,
        pickedIcon: true,
    });

  }



    render() {
      if (this.state.pickedIcon) {
        return startGame(this.state.emoji);
      }
      let prompt = 'Choose your player icon!';


      return (
        <div className= "wrapper">
          <h2 className="board-heading">{prompt}</h2>
          <div className="icon-row">
          {this.state.emojiList.map((emoji) => this.renderIcon(emoji))}
          </div>
          <div className="game-info">
            <div>{/* status */}</div>
            <ol>{/* TODO */}</ol>
          </div>
        </div>
      );
    }
  }

  function startGame(emoji){
    return  <Redirect to={{pathname: "/gameStart" , state: {playerIcon: emoji} }} />
}
